import {Document, model, Schema} from 'mongoose';

export interface ContributionInterface extends Document {
  title: string;
  description: string;
  author: Schema.Types.ObjectId;
  noteRef: Schema.Types.ObjectId;
  changes: string;
  approved: boolean;
  created: Date;
}

export const ContributionSchema = new Schema<ContributionInterface>({
  title: {
    type: String,
    required: true,
    trim: true,
  },
  description: {
    type: String,
    trim: true,
  },
  author: {
    type: Schema.Types.ObjectId,
    ref: 'User',
    required: true,
  },
  noteRef: {
    type: Schema.Types.ObjectId,
    ref: 'Note',
    required: true,
  },
  changes: {
    type: String,
    required: true,
    trim: true,
  },
  approved: {
    type: Boolean,
    default: false,
  },
  created: {
    type: Date,
    default: Date.now,
  },
});

export const Contribution = model<ContributionInterface>('Contribution', ContributionSchema);

import { User } from './user';
import { Note } from './note';
import { Contribution } from './contribution';

const user = User;
const note = Note;
const contribution = Contribution;

export const db = {
  user,
  note,
  contribution
};

import {Document, model, Schema} from 'mongoose';

export interface UserInterface extends Document {
  username: string;
  name: string;
  email: string;
  reputation: number;
  password: string;
  created: Date;
}

const UserSchema = new Schema<UserInterface>({
  username: {
    type: String,
    unique: true,
    match: [/^[a-zA-Z0-9]+$/, 'is invalid'],
    required: true,
  },
  name: {
    type: String,
    trim: true,
    required: true,
  },
  email: {
    type: String,
    lowercase: true,
    unique: true,
    match: [/^[a-zA-Z0-9.!#$%&'*+\/=?^_`{|}~-]+@[a-zA-Z0-9](?:[a-zA-Z0-9-]{0,61}[a-zA-Z0-9])?(?:\.[a-zA-Z0-9](?:[a-zA-Z0-9-]{0,61}[a-zA-Z0-9])?)*$/, 'is invalid'],
    required: true,
  },
  reputation: {
    type: Number,
    default: 0,
    min: 0,
    max: 100,
  },
  password: {
    type: String,
    required: true,
  },
  created: {
    type: Date,
    default: Date.now,
  },
});

export const User = model<UserInterface>('User', UserSchema);

import {Document, model, Schema} from 'mongoose';

type permission = 'read' | 'read-write' | 'admin';

export interface NoteInterface extends Document {
  title: string;
  content: string;
  author: Schema.Types.ObjectId;
  collaborators: Schema.Types.ObjectId[];
  rating: number[];
  tags: string[];
  isPublic: boolean;
  sharedWith: [{ user: Schema.Types.ObjectId; permission: permission }];
  created: Date;
}

export const NoteSchema = new Schema<NoteInterface>({
  title: {
    type: String,
    required: true,
    trim: true,
  },
  content: {
    type: String,
    required: true,
    trim: true,
  },
  author: {
    type: Schema.Types.ObjectId,
    ref: 'User',
    required: true,
  },
  collaborators: [
    {
      type: Schema.Types.ObjectId,
      ref: 'User',
    },
  ],
  rating: [
    {
      type: Number,
      min: 0,
      max: 5,
    },
  ],
  tags: [
    {
      type: String,
      trim: true,
    },
  ],
  isPublic: {
    type: Boolean,
    default: false,
  },
  sharedWith: [
    {
      user: {
        type: Schema.Types.ObjectId,
        ref: 'User',
      },
      permission: {
        type: String,
        enum: ['read', 'read-write', 'admin'],
      },
    },
  ],
  created: {
    type: Date,
    default: Date.now,
  },
});

export const Note = model<NoteInterface>('Note', NoteSchema);
